import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import Header from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Users, MapPin, ChevronLeft, ChevronRight } from "lucide-react";
import type { User } from "@shared/schema";

export default function DistrictAdminList() {
  const [currentPage, setCurrentPage] = useState(0);
  const limit = 10;

  const { data: users, isLoading, error } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  console.log("Users data:", users, "Loading:", isLoading, "Error:", error);

  const districtAdmins = users?.filter(user => user.role === 'district_admin') || [];
  const totalPages = Math.ceil(districtAdmins.length / limit);
  const startIndex = currentPage * limit;
  const endIndex = startIndex + limit;
  const currentAdmins = districtAdmins.slice(startIndex, endIndex);

  const getStatusBadge = (user: User) => {
    return <Badge variant="secondary" className="bg-green-100 text-green-800">Active</Badge>;
  };

  return (
    <div className="flex-1 flex flex-col">
      <Header 
        title="District Administrators" 
        breadcrumbs={[
          { name: "Home" },
          { name: "District Administrators" }
        ]}
      />

      <main className="flex-1 p-6 overflow-auto">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <div className="flex items-center">
                <Users className="w-5 h-5 mr-2 text-primary" />
                District Admin Accounts
              </div>
              <div className="text-right">
                <p className="text-sm text-muted-foreground">Total Districts</p>
                <p className="text-2xl font-bold" data-testid="total-district-admins">{districtAdmins.length}</p>
              </div>
            </CardTitle>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="flex items-center justify-center p-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
              </div>
            ) : (
              <div className="space-y-4">
                <div className="rounded-md border">
                  <Table>
                    <TableHeader>
                      <TableRow>
                        <TableHead>Counseling District</TableHead>
                        <TableHead>Username</TableHead>
                        <TableHead>District Admin Name</TableHead>
                        <TableHead>Status</TableHead>
                        <TableHead>Actions</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {currentAdmins.map((admin: User) => (
                        <TableRow key={admin.id} data-testid={`admin-row-${admin.district}`}>
                          <TableCell className="font-medium">
                            <div className="flex items-center">
                              <MapPin className="w-4 h-4 mr-2 text-muted-foreground" />
                              {admin.district || 'N/A'}
                            </div>
                          </TableCell>
                          <TableCell className="font-mono text-sm">{admin.username}</TableCell>
                          <TableCell>{admin.firstName && admin.lastName ? `${admin.firstName} ${admin.lastName}` : 'N/A'}</TableCell>
                          <TableCell>{getStatusBadge(admin)}</TableCell>
                          <TableCell>
                            <Button variant="outline" size="sm" data-testid={`button-edit-${admin.district}`}>
                              Edit
                            </Button>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </div>

                {!isLoading && districtAdmins.length === 0 && (
                  <div className="text-center py-8">
                    <p className="text-muted-foreground">No district administrators found.</p>
                    {users && (
                      <p className="text-xs text-muted-foreground mt-2">
                        Total users loaded: {users.length}
                      </p>
                    )}
                  </div>
                )}

                {/* Pagination Controls */}
                {totalPages > 1 && (
                  <div className="flex items-center justify-between border-t pt-4">
                    <p className="text-sm text-muted-foreground">
                      Showing {startIndex + 1}-{Math.min(endIndex, districtAdmins.length)} of {districtAdmins.length} district administrators
                    </p>
                    <div className="flex items-center space-x-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setCurrentPage(prev => Math.max(0, prev - 1))}
                        disabled={currentPage === 0}
                        data-testid="button-prev-page"
                      >
                        <ChevronLeft className="w-4 h-4" />
                      </Button>
                      <span className="text-sm font-medium">
                        {currentPage + 1} / {totalPages}
                      </span>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setCurrentPage(prev => Math.min(totalPages - 1, prev + 1))}
                        disabled={currentPage === totalPages - 1}
                        data-testid="button-next-page"
                      >
                        <ChevronRight className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                )}

                <div className="flex items-center justify-between pt-2">
                  <Badge variant="outline" className="text-xs">
                    All districts covered
                  </Badge>
                </div>
              </div>
            )}
          </CardContent>
        </Card>
      </main>
    </div>
  );
}

client/src/pages/student-details.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { useParams, Link } from "wouter";
import Header from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { ArrowLeft, User, GraduationCap, MapPin, CheckCircle, XCircle } from "lucide-react";
import type { Student } from "@shared/schema";

export default function StudentDetails() {
  const params = useParams();
  const studentId = params.id as string;

  const { data: student, isLoading } = useQuery<Student>({
    queryKey: [`/api/students/${studentId}`],
  });

  if (isLoading) {
    return (
      <div className="flex-1 flex flex-col">
        <Header title="Student Details" />
        <main className="flex-1 p-6 flex items-center justify-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
        </main>
      </div>
    );
  }

  if (!student) {
    return (
      <div className="flex-1 flex flex-col">
        <Header title="Student Details" />
        <main className="flex-1 p-6 flex items-center justify-center">
          <div className="text-center">
            <p className="text-muted-foreground">Student not found</p>
            <Link href="/students">
              <Button className="mt-4">Back to Students</Button>
            </Link>
          </div>
        </main>
      </div>
    );
  }

  const choices = [
    student.choice1,
    student.choice2,
    student.choice3,
    student.choice4,
    student.choice5,
    student.choice6,
    student.choice7,
    student.choice8,
    student.choice9,
    student.choice10,
  ].filter(Boolean) as string[];

  const getChoiceStatus = (choice: string) => {
    if (student.allottedDistrict === choice) {
      return { status: 'allocated', color: 'bg-green-100 text-green-800', icon: CheckCircle };
    }
    return { status: 'not-allocated', color: 'bg-gray-100 text-gray-600', icon: XCircle };
  };

  return (
    <div className="flex-1 flex flex-col">
      <Header 
        title="Student Details"
        breadcrumbs={[
          { name: "Home", href: "/" },
          { name: "Students", href: "/students" },
          { name: student.name }
        ]}
      />

      <main className="flex-1 p-6 overflow-auto">
        <div className="max-w-4xl mx-auto space-y-6">
          {/* Back Button */}
          <Link href="/students">
            <Button variant="outline" data-testid="button-back-students" className="mb-4">
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to Students
            </Button>
          </Link>
          {/* Student Information */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <User className="w-5 h-5 mr-2 text-primary" />
                Student Information
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-muted-foreground">Application Number</p>
                  <p className="font-medium" data-testid="text-app-no">{student.appNo}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Merit Number</p>
                  <p className="font-medium" data-testid="text-merit-no">{student.meritNumber}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Full Name</p>
                  <p className="font-medium" data-testid="text-student-name">{student.name}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Stream</p>
                  <Badge variant="secondary" className="font-medium" data-testid="badge-stream">
                    <GraduationCap className="w-3 h-3 mr-1" />
                    {student.stream}
                  </Badge>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Allocation Status */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <MapPin className="w-5 h-5 mr-2 text-primary" />
                Allocation Status
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-muted-foreground">Status</p>
                  <Badge 
                    variant={student.allocationStatus === 'allotted' ? 'default' : 'secondary'}
                    className={
                      student.allocationStatus === 'allotted' 
                        ? 'bg-green-100 text-green-800' 
                        : student.allocationStatus === 'not_allotted' 
                        ? 'bg-red-100 text-red-800' 
                        : 'bg-amber-100 text-amber-800'
                    }
                    data-testid="badge-allocation-status"
                  >
                    {student.allocationStatus === 'allotted' ? 'Allocated' : 
                     student.allocationStatus === 'not_allotted' ? 'Not Allocated' : 'Pending'}
                  </Badge>
                </div>
                {student.allottedDistrict && (
                  <div>
                    <p className="text-sm text-muted-foreground">Allocated Station</p>
                    <p className="font-medium flex items-center" data-testid="text-allocated-station">
                      <MapPin className="w-4 h-4 mr-1 text-primary" />
                      {student.allottedDistrict}
                    </p>
                  </div>
                )}
                {student.allottedStream && (
                  <div>
                    <p className="text-sm text-muted-foreground">Allocated Stream</p>
                    <Badge variant="secondary" className="font-medium" data-testid="badge-allocated-stream">
                      {student.allottedStream}
                    </Badge>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          {/* District Choices */}
          <Card>
            <CardHeader>
              <CardTitle>District Preferences</CardTitle>
              <p className="text-sm text-muted-foreground">
                Student's preferences in order of priority
              </p>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-3">
                {choices.map((choice, index) => {
                  const { status, color, icon: StatusIcon } = getChoiceStatus(choice);
                  return (
                    <div
                      key={index}
                      className={`p-3 rounded-lg border ${
                        student.allottedDistrict === choice
                          ? 'border-green-200 bg-green-50'
                          : 'border-border bg-card'
                      }`}
                      data-testid={`choice-${index + 1}`}
                    >
                      <div className="flex items-center justify-between">
                        <div className="flex items-center">
                          <span className="text-xs text-muted-foreground mr-2">
                            {index + 1}.
                          </span>
                          <span className="font-medium">{choice}</span>
                        </div>
                        {student.allottedDistrict === choice && (
                          <StatusIcon className="w-4 h-4 text-green-600" />
                        )}
                      </div>
                    </div>
                  );
                })}
              </div>

              {choices.length === 0 && (
                <div className="text-center py-8">
                  <p className="text-muted-foreground">No district preferences recorded</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

client/src/pages/manage-district-admins..content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import Header from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { PasswordInput } from "@/components/ui/password-input";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { DataPagination } from "@/components/ui/data-pagination";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Users, 
  Plus, 
  Edit2, 
  Trash2, 
  Save, 
  X,
  UserPlus,
  Ban,
  Shield,
  Key
} from "lucide-react";
import type { User } from "@shared/schema";
import { DISTRICTS } from "@shared/schema";

const createAdminSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  district: z.enum(DISTRICTS as any),
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  email: z.string().email("Valid email is required"),
});

const updateAdminSchema = createAdminSchema.partial().omit({ password: true });

const passwordResetSchema = z.object({
  newPassword: z.string().min(6, "Password must be at least 6 characters"),
  confirmPassword: z.string().min(6, "Password confirmation is required"),
}).refine(data => data.newPassword === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

export default function ManageDistrictAdmins() {
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [editingAdmin, setEditingAdmin] = useState<User | null>(null);
  const [resetPasswordAdmin, setResetPasswordAdmin] = useState<User | null>(null);
  const [currentPage, setCurrentPage] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(25);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: users, isLoading } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  const districtAdmins = users?.filter(user => user.role === 'district_admin') || [];

  // Pagination logic
  const totalAdmins = districtAdmins.length;
  const startIndex = currentPage * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const paginatedAdmins = districtAdmins.slice(startIndex, endIndex);

  const createForm = useForm<z.infer<typeof createAdminSchema>>({
    resolver: zodResolver(createAdminSchema),
    defaultValues: {
      username: '',
      password: '',
      district: undefined,
      firstName: '',
      lastName: '',
      email: '',
    },
  });

  const editForm = useForm<z.infer<typeof updateAdminSchema>>({
    resolver: zodResolver(updateAdminSchema),
    defaultValues: {
      username: '',
      district: undefined,
      firstName: '',
      lastName: '',
      email: '',
    },
  });

  const passwordResetForm = useForm<z.infer<typeof passwordResetSchema>>({
    resolver: zodResolver(passwordResetSchema),
    defaultValues: {
      newPassword: '',
      confirmPassword: '',
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: z.infer<typeof createAdminSchema>) => {
      await apiRequest("POST", "/api/users", {
        ...data,
        role: 'district_admin',
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      setShowCreateDialog(false);
      createForm.reset();
      toast({
        title: "Admin Created",
        description: "District administrator has been created successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Creation Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string, data: z.infer<typeof updateAdminSchema> }) => {
      await apiRequest("PUT", `/api/users/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      setEditingAdmin(null);
      editForm.reset();
      toast({
        title: "Admin Updated",
        description: "District administrator has been updated successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Update Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/users/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Admin Deleted",
        description: "District administrator has been deleted successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Deletion Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const blockMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("PUT", `/api/users/${id}/block`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Admin Blocked",
        description: "District administrator has been blocked successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Block Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const unblockMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("PUT", `/api/users/${id}/unblock`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Admin Unblocked",
        description: "District administrator has been unblocked successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Unblock Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const resetPasswordMutation = useMutation({
    mutationFn: async ({ id, newPassword }: { id: string, newPassword: string }) => {
      await apiRequest("PUT", `/api/users/${id}/reset-password`, { newPassword });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      setResetPasswordAdmin(null);
      passwordResetForm.reset();
      toast({
        title: "Password Reset",
        description: "District administrator password has been reset successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Password Reset Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const startEdit = (admin: User) => {
    setEditingAdmin(admin);
    editForm.reset({
      username: admin.username,
      district: admin.district as any,
      firstName: admin.firstName || '',
      lastName: admin.lastName || '',
      email: admin.email || '',
    });
  };

  const cancelEdit = () => {
    setEditingAdmin(null);
    editForm.reset();
  };

  const onCreateSubmit = (values: z.infer<typeof createAdminSchema>) => {
    createMutation.mutate(values);
  };

  const onEditSubmit = (values: z.infer<typeof updateAdminSchema>) => {
    if (editingAdmin) {
      updateMutation.mutate({ id: editingAdmin.id, data: values });
    }
  };

  const onPasswordResetSubmit = (values: z.infer<typeof passwordResetSchema>) => {
    if (resetPasswordAdmin) {
      resetPasswordMutation.mutate({ id: resetPasswordAdmin.id, newPassword: values.newPassword });
    }
  };

  const startPasswordReset = (admin: User) => {
    setResetPasswordAdmin(admin);
    passwordResetForm.reset();
  };


  return (
    <div className="flex-1 flex flex-col">
      <Header 
        title="Manage District Administrators" 
        breadcrumbs={[
          { name: "Home", href: "/" },
          { name: "Manage District Administrators" }
        ]}
      />

      <main className="flex-1 p-6 overflow-auto">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <div className="flex items-center">
                <Users className="w-5 h-5 mr-2 text-primary" />
                District Administrators
              </div>
              <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
                <DialogTrigger asChild>
                  <Button data-testid="button-add-admin">
                    <Plus className="w-4 h-4 mr-2" />
                    Add District Admin
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>
                      <UserPlus className="w-5 h-5 mr-2 inline" />
                      Create District Administrator
                    </DialogTitle>
                  </DialogHeader>
                  <Form {...createForm}>
                    <form onSubmit={createForm.handleSubmit(onCreateSubmit)} className="space-y-4">
                      <div className="grid grid-cols-2 gap-4">
                        <FormField
                          control={createForm.control}
                          name="firstName"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>First Name</FormLabel>
                              <FormControl>
                                <Input placeholder="Enter first name" {...field} data-testid="input-first-name" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                        <FormField
                          control={createForm.control}
                          name="lastName"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Last Name</FormLabel>
                              <FormControl>
                                <Input placeholder="Enter last name" {...field} data-testid="input-last-name" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>
                      <FormField
                        control={createForm.control}
                        name="email"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Email</FormLabel>
                            <FormControl>
                              <Input type="email" placeholder="Enter email address" {...field} data-testid="input-email" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={createForm.control}
                        name="username"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Username</FormLabel>
                            <FormControl>
                              <Input placeholder="Enter username" {...field} data-testid="input-username" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={createForm.control}
                        name="password"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Password</FormLabel>
                            <FormControl>
                              <PasswordInput placeholder="Enter password" {...field} data-testid="input-password" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={createForm.control}
                        name="district"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>District</FormLabel>
                            <Select onValueChange={field.onChange} value={field.value}>
                              <FormControl>
                                <SelectTrigger data-testid="select-district">
                                  <SelectValue placeholder="Select district" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                {DISTRICTS.map(district => (
                                  <SelectItem key={district} value={district}>
                                    {district}
                                  </SelectItem>
                                ))}
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <div className="flex justify-end space-x-2 pt-4">
                        <Button type="button" variant="outline" onClick={() => setShowCreateDialog(false)}>
                          Cancel
                        </Button>
                        <Button type="submit" disabled={createMutation.isPending} data-testid="button-save-admin">
                          {createMutation.isPending ? "Creating..." : "Create Admin"}
                        </Button>
                      </div>
                    </form>
                  </Form>
                </DialogContent>
              </Dialog>
            </CardTitle>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="flex items-center justify-center p-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
              </div>
            ) : (
              <div className="space-y-4">
                <div className="rounded-md border">
                  <Table>
                    <TableHeader>
                      <TableRow>
                        <TableHead>District Admin Name</TableHead>
                        <TableHead>Username</TableHead>
                        <TableHead>Email</TableHead>
                        <TableHead>Counseling District</TableHead>
                        <TableHead>Status</TableHead>
                        <TableHead className="w-32">Actions</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {paginatedAdmins.map((admin) => (
                        <TableRow key={admin.id}>
                          <TableCell>
                            <div className="font-medium" data-testid={`text-name-${admin.id}`}>
                              {admin.firstName} {admin.lastName}
                            </div>
                          </TableCell>
                          <TableCell data-testid={`text-username-${admin.id}`}>
                            {admin.username}
                          </TableCell>
                          <TableCell data-testid={`text-email-${admin.id}`}>
                            {admin.email || 'N/A'}
                          </TableCell>
                          <TableCell data-testid={`text-district-${admin.id}`}>
                            <Badge variant="outline">{admin.district}</Badge>
                          </TableCell>
                          <TableCell data-testid={`status-${admin.id}`}>
                            {admin.isBlocked ? (
                              <Badge variant="destructive">
                                Blocked
                              </Badge>
                            ) : (
                              <Badge variant="secondary" className="bg-green-100 text-green-800">
                                Active
                              </Badge>
                            )}
                          </TableCell>
                          <TableCell>
                            <div className="flex space-x-2">
                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => startEdit(admin)}
                                data-testid={`button-edit-${admin.id}`}
                              >
                                <Edit2 className="w-3 h-3" />
                              </Button>

                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => startPasswordReset(admin)}
                                className="text-blue-600 hover:text-blue-700"
                                data-testid={`button-reset-password-${admin.id}`}
                              >
                                <Key className="w-3 h-3" />
                              </Button>

                              {admin.isBlocked ? (
                                <AlertDialog>
                                  <AlertDialogTrigger asChild>
                                    <Button
                                      size="sm"
                                      variant="outline"
                                      className="text-green-600 hover:text-green-700"
                                      data-testid={`button-unblock-${admin.id}`}
                                    >
                                      <Shield className="w-3 h-3" />
                                    </Button>
                                  </AlertDialogTrigger>
                                  <AlertDialogContent>
                                    <AlertDialogHeader>
                                      <AlertDialogTitle>Unblock Administrator</AlertDialogTitle>
                                      <AlertDialogDescription>
                                        Are you sure you want to unblock {admin.firstName} {admin.lastName}? 
                                        They will be able to access the system again.
                                      </AlertDialogDescription>
                                    </AlertDialogHeader>
                                    <AlertDialogFooter>
                                      <AlertDialogCancel>Cancel</AlertDialogCancel>
                                      <AlertDialogAction
                                        onClick={() => unblockMutation.mutate(admin.id)}
                                        disabled={unblockMutation.isPending}
                                        data-testid={`confirm-unblock-${admin.id}`}
                                      >
                                        {unblockMutation.isPending ? "Unblocking..." : "Unblock"}
                                      </AlertDialogAction>
                                    </AlertDialogFooter>
                                  </AlertDialogContent>
                                </AlertDialog>
                              ) : (
                                <AlertDialog>
                                  <AlertDialogTrigger asChild>
                                    <Button
                                      size="sm"
                                      variant="outline"
                                      className="text-orange-600 hover:text-orange-700"
                                      data-testid={`button-block-${admin.id}`}
                                    >
                                      <Ban className="w-3 h-3" />
                                    </Button>
                                  </AlertDialogTrigger>
                                  <AlertDialogContent>
                                    <AlertDialogHeader>
                                      <AlertDialogTitle>Block Administrator</AlertDialogTitle>
                                      <AlertDialogDescription>
                                        Are you sure you want to block {admin.firstName} {admin.lastName}? 
                                        They will not be able to access the system until unblocked.
                                      </AlertDialogDescription>
                                    </AlertDialogHeader>
                                    <AlertDialogFooter>
                                      <AlertDialogCancel>Cancel</AlertDialogCancel>
                                      <AlertDialogAction
                                        onClick={() => blockMutation.mutate(admin.id)}
                                        disabled={blockMutation.isPending}
                                        data-testid={`confirm-block-${admin.id}`}
                                        className="bg-orange-600 hover:bg-orange-700"
                                      >
                                        {blockMutation.isPending ? "Blocking..." : "Block"}
                                      </AlertDialogAction>
                                    </AlertDialogFooter>
                                  </AlertDialogContent>
                                </AlertDialog>
                              )}

                              <AlertDialog>
                                <AlertDialogTrigger asChild>
                                  <Button
                                    size="sm"
                                    variant="outline"
                                    className="text-red-600 hover:text-red-700"
                                    data-testid={`button-delete-${admin.id}`}
                                  >
                                    <Trash2 className="w-3 h-3" />
                                  </Button>
                                </AlertDialogTrigger>
                                <AlertDialogContent>
                                  <AlertDialogHeader>
                                    <AlertDialogTitle>Delete Administrator</AlertDialogTitle>
                                    <AlertDialogDescription>
                                      Are you sure you want to delete {admin.firstName} {admin.lastName}? 
                                      This action cannot be undone.
                                    </AlertDialogDescription>
                                  </AlertDialogHeader>
                                  <AlertDialogFooter>
                                    <AlertDialogCancel>Cancel</AlertDialogCancel>
                                    <AlertDialogAction
                                      onClick={() => deleteMutation.mutate(admin.id)}
                                      disabled={deleteMutation.isPending}
                                      data-testid={`confirm-delete-${admin.id}`}
                                      className="bg-red-600 hover:bg-red-700"
                                    >
                                      {deleteMutation.isPending ? "Deleting..." : "Delete"}
                                    </AlertDialogAction>
                                  </AlertDialogFooter>
                                </AlertDialogContent>
                              </AlertDialog>
                            </div>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </div>

                <DataPagination
                  currentPage={currentPage}
                  totalItems={totalAdmins}
                  itemsPerPage={itemsPerPage}
                  onPageChange={setCurrentPage}
                  onItemsPerPageChange={(newLimit) => {
                    setItemsPerPage(newLimit);
                    setCurrentPage(0); // Reset to first page when changing items per page
                  }}
                  showItemsPerPageSelector={true}
                  itemsPerPageOptions={[10, 25, 50, 100]}
                />
              </div>
            )}
          </CardContent>
        </Card>

        {/* Edit Dialog */}
        <Dialog open={!!editingAdmin} onOpenChange={(open) => !open && cancelEdit()}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>
                <Edit2 className="w-5 h-5 mr-2 inline" />
                Edit District Administrator
              </DialogTitle>
            </DialogHeader>
            <Form {...editForm}>
              <form onSubmit={editForm.handleSubmit(onEditSubmit)} className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={editForm.control}
                    name="firstName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>First Name</FormLabel>
                        <FormControl>
                          <Input placeholder="Enter first name" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={editForm.control}
                    name="lastName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Last Name</FormLabel>
                        <FormControl>
                          <Input placeholder="Enter last name" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>
                <FormField
                  control={editForm.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email</FormLabel>
                      <FormControl>
                        <Input type="email" placeholder="Enter email address" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={editForm.control}
                  name="username"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Username</FormLabel>
                      <FormControl>
                        <Input placeholder="Enter username" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={editForm.control}
                  name="district"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>District</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select district" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {DISTRICTS.map(district => (
                            <SelectItem key={district} value={district}>
                              {district}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2 pt-4">
                  <Button type="button" variant="outline" onClick={cancelEdit}>
                    Cancel
                  </Button>
                  <Button type="submit" disabled={updateMutation.isPending}>
                    {updateMutation.isPending ? "Updating..." : "Update Admin"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>

        {/* Password Reset Dialog */}
        <Dialog open={!!resetPasswordAdmin} onOpenChange={(open) => !open && setResetPasswordAdmin(null)}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>
                <Key className="w-5 h-5 mr-2 inline" />
                Reset Password for {resetPasswordAdmin?.firstName} {resetPasswordAdmin?.lastName}
              </DialogTitle>
              <DialogDescription>
                Enter a new password for this district administrator. The password must be at least 6 characters long.
              </DialogDescription>
            </DialogHeader>
            <Form {...passwordResetForm}>
              <form onSubmit={passwordResetForm.handleSubmit(onPasswordResetSubmit)} className="space-y-4">
                <FormField
                  control={passwordResetForm.control}
                  name="newPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>New Password</FormLabel>
                      <FormControl>
                        <PasswordInput 
                          placeholder="Enter new password"
                          data-testid="input-new-password"
                          {...field} 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={passwordResetForm.control}
                  name="confirmPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Confirm New Password</FormLabel>
                      <FormControl>
                        <PasswordInput 
                          placeholder="Confirm new password"
                          data-testid="input-confirm-password"
                          {...field} 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2 pt-4">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setResetPasswordAdmin(null)}
                  >
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={resetPasswordMutation.isPending}
                    data-testid="button-confirm-reset-password"
                  >
                    {resetPasswordMutation.isPending ? "Resetting..." : "Reset Password"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </main>
    </div>
  );
}

client/src/pages/student-preference-mana.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import Header from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle } from "@/components/ui/alert-dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { 
  Search, 
  UserCog, 
  Edit, 
  Save, 
  X, 
  Clock,
  AlertTriangle,
  CheckCircle,
  Eye,
  Lock,
  Unlock,
  XCircle,
} from "lucide-react";
import type { Student } from "@shared/schema";
import { SCHOOL_DISTRICTS, COUNSELING_DISTRICTS } from "@shared/schema";

const DISTRICTS = SCHOOL_DISTRICTS;
const STREAMS = ["Medical", "NonMedical", "Commerce"];

const updatePreferencesSchema = z.object({
  stream: z.enum(['Medical', 'Commerce', 'NonMedical']),
  choice1: z.string().transform(val => val === " " ? "" : val).optional(),
  choice2: z.string().transform(val => val === " " ? "" : val).optional(),
  choice3: z.string().transform(val => val === " " ? "" : val).optional(),
  choice4: z.string().transform(val => val === " " ? "" : val).optional(),
  choice5: z.string().transform(val => val === " " ? "" : val).optional(),
  choice6: z.string().transform(val => val === " " ? "" : val).optional(),
  choice7: z.string().transform(val => val === " " ? "" : val).optional(),
  choice8: z.string().transform(val => val === " " ? "" : val).optional(),
  choice9: z.string().transform(val => val === " " ? "" : val).optional(),
  choice10: z.string().transform(val => val === " " ? "" : val).optional(),
});

export default function StudentPreferenceManagement() {
  const [searchTerm, setSearchTerm] = useState("");
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isChoicesModalOpen, setIsChoicesModalOpen] = useState(false);
  const [selectedStudentForEdit, setSelectedStudentForEdit] = useState<Student | null>(null);
  const [selectedStudentForChoices, setSelectedStudentForChoices] = useState<Student | null>(null);

  // Confirmation dialog states
  const [isLockConfirmDialogOpen, setIsLockConfirmDialogOpen] = useState(false);
  const [isUnlockConfirmDialogOpen, setIsUnlockConfirmDialogOpen] = useState(false);
  const [isReleaseConfirmDialogOpen, setIsReleaseConfirmDialogOpen] = useState(false);
  const [selectedStudentForLock, setSelectedStudentForLock] = useState<Student | null>(null);
  const [selectedStudentForUnlock, setSelectedStudentForUnlock] = useState<Student | null>(null);
  const [selectedStudentForRelease, setSelectedStudentForRelease] = useState<Student | null>(null);

  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Helper function to check if student preferences are complete
  const areAllPreferencesFilled = (student: Student) => {
    if (!student.stream || !student.stream.trim()) return false;

    const choices = [
      student.choice1, student.choice2, student.choice3, student.choice4, student.choice5,
      student.choice6, student.choice7, student.choice8, student.choice9, student.choice10
    ];

    return choices.every(choice => choice && choice.trim());
  };

  // Form setup for edit modal
  const form = useForm({
    resolver: zodResolver(updatePreferencesSchema),
    defaultValues: {
      stream: "NonMedical" as const,
      choice1: "",
      choice2: "",
      choice3: "",
      choice4: "",
      choice5: "",
      choice6: "",
      choice7: "",
      choice8: "",
      choice9: "",
      choice10: "",
    },
  });

  // Lock student for editing mutation
  const lockForEditMutation = useMutation({
    mutationFn: async (studentId: string) => {
      const response = await apiRequest('POST', `/api/students/${studentId}/lock`);
      return await response.json();
    },
    onSuccess: (updatedStudent: Student) => {
      queryClient.invalidateQueries({ queryKey: ["/api/students"] });
      toast({
        title: "Success",
        description: "Student locked for editing",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to lock student",
        variant: "destructive",
      });
    }
  });

  // Unlock student mutation  
  const unlockEditMutation = useMutation({
    mutationFn: async (studentId: string) => {
      const response = await apiRequest('POST', `/api/students/${studentId}/unlock`);
      return await response.json();
    },
    onSuccess: (updatedStudent: Student) => {
      queryClient.invalidateQueries({ queryKey: ["/api/students"] });
      toast({
        title: "Success", 
        description: "Student unlocked",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to unlock student",
        variant: "destructive",
      });
    }
  });

  // Update preferences mutation
  const updatePreferencesMutation = useMutation({
    mutationFn: async (data: { studentId: string, preferences: any }) => {
      const response = await apiRequest('PUT', `/api/students/${data.studentId}/preferences`, data.preferences);
      return await response.json();
    },
    onSuccess: (updatedStudent: Student) => {
      queryClient.invalidateQueries({ queryKey: ["/api/students"] });
      setIsEditModalOpen(false);
      setSelectedStudentForEdit(null);
      form.reset();
      toast({
        title: "Success",
        description: "Student preferences updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update preferences",
        variant: "destructive",
      });
    }
  });

  const { data: studentsData, isLoading } = useQuery({
    queryKey: ["/api/students", { limit: 200, offset: 0 }],
  });

  // Query settings to check if allocation is finalized
  const { data: settingsData } = useQuery({
    queryKey: ["/api/settings"],
  });

  // Check if allocation is finalized
  const isAllocationFinalized = Array.isArray(settingsData) && settingsData.some((setting: any) => 
    setting.key === 'allocation_finalized' && setting.value === 'true'
  );

  const filteredStudents = (studentsData as any)?.students?.filter((student: Student) => 
    student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.meritNumber.toString().includes(searchTerm) ||
    student.appNo?.includes(searchTerm) ||
    student.counselingDistrict?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.districtAdmin?.toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];


  // Release assignment mutation
  const releaseAssignmentMutation = useMutation({
    mutationFn: async (studentId: string) => {
      const response = await apiRequest('POST', `/api/students/${studentId}/release-assignment`);
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/students'] });
      toast({
        title: "Assignment Released",
        description: "Student assignment has been cleared successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Release Failed",
        description: error.message || "Failed to release assignment",
        variant: "destructive",
      });
    }
  });

  // Finalize allocation mutation
  const finalizeAllocationMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', '/api/allocation/finalize');
      return await response.json();
    },
    onSuccess: (result: any) => {
      queryClient.invalidateQueries({ queryKey: ["/api/students"] });
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
      toast({
        title: "Success",
        description: "Allocation process finalized successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to finalize allocation",
        variant: "destructive",
      });
    }
  });

  // Confirmation functions for actions
  const confirmLockStudent = () => {
    if (!selectedStudentForLock) return;

    lockForEditMutation.mutate(selectedStudentForLock.id);
    setIsLockConfirmDialogOpen(false);
    setSelectedStudentForLock(null);
  };

  const confirmUnlockStudent = () => {
    if (!selectedStudentForUnlock) return;

    unlockEditMutation.mutate(selectedStudentForUnlock.id);
    setIsUnlockConfirmDialogOpen(false);
    setSelectedStudentForUnlock(null);
  };

  const confirmReleaseAssignment = () => {
    if (!selectedStudentForRelease) return;

    releaseAssignmentMutation.mutate(selectedStudentForRelease.id);
    setIsReleaseConfirmDialogOpen(false);
    setSelectedStudentForRelease(null);
  };

  const openEditModal = (student: Student) => {
    // Directly open edit modal without locking
    setSelectedStudentForEdit(student);
    form.reset({
      stream: student.stream as any || "NonMedical",
      choice1: student.choice1 || '',
      choice2: student.choice2 || '',
      choice3: student.choice3 || '',
      choice4: student.choice4 || '',
      choice5: student.choice5 || '',
      choice6: student.choice6 || '',
      choice7: student.choice7 || '',
      choice8: student.choice8 || '',
      choice9: student.choice9 || '',
      choice10: student.choice10 || '',
    });
    setIsEditModalOpen(true);
  };

  const openChoicesModal = (student: Student) => {
    setSelectedStudentForChoices(student);
    setIsChoicesModalOpen(true);
  };

  const handleModalSave = (data: any) => {
    if (!selectedStudentForEdit) return;
    updatePreferencesMutation.mutate({
      studentId: selectedStudentForEdit.id,
      preferences: data
    });
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'allotted':
        return <Badge variant="secondary" className="bg-green-100 text-green-800">Allotted</Badge>;
      case 'not_allotted':
        return <Badge variant="destructive">Not Allotted</Badge>;
      case 'pending':
        return <Badge variant="outline">Pending</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  // Helper function to determine if current user can edit a specific student
  const canEditStudent = (student: Student) => {
    if (!user) return false;

    // Central admin can edit all students
    if (user.role === 'central_admin') {
      return true;
    }

    // District admin logic
    if (user.role === 'district_admin') {
      // If student has no assigned district admin (N/A), any district admin can edit
      if (!student.districtAdmin) {
        return true;
      }

      // Check if student belongs to this district
      const belongsToDistrict = student.counselingDistrict === user.district;

      // If student has an assigned district admin, only that specific admin can edit
      return student.districtAdmin === user.username && belongsToDistrict;
    }

    return false;
  };


  return (
    <div className="flex-1 flex flex-col">
      <Header 
        title="Student Preference Management" 
        breadcrumbs={[
          { name: "Home" },
          { name: "Student Preference Management" }
        ]}
      />

      <main className="flex-1 p-6 overflow-auto">
        <div className="space-y-6">
          {/* Header with Search */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <div className="flex items-center">
                  <UserCog className="w-5 h-5 mr-2 text-primary" />
                  Student Preferences - Central Admin View
                </div>
                {user?.role === 'central_admin' && (
                  <Button 
                    variant={isAllocationFinalized ? "outline" : "default"}
                    size="sm"
                    onClick={() => {
                      if (!isAllocationFinalized && window.confirm('Are you sure you want to finalize the allocation process? This action cannot be undone.')) {
                        finalizeAllocationMutation.mutate();
                      }
                    }}
                    disabled={finalizeAllocationMutation.isPending || isAllocationFinalized}
                    data-testid="button-finalize-allocation"
                    className={isAllocationFinalized 
                      ? "bg-gray-100 text-gray-600 cursor-not-allowed" 
                      : "bg-green-600 hover:bg-green-700 text-white"
                    }
                  >
                    <CheckCircle className="w-4 h-4 mr-2" />
                    {finalizeAllocationMutation.isPending 
                      ? "Finalizing..." 
                      : isAllocationFinalized 
                        ? "Allocation Finalized" 
                        : "Finalize Allocation"
                    }
                  </Button>
                )}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center space-x-4 mb-4">
                <div className="relative flex-1">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                  <Input
                    placeholder="Search by name, merit number, app number, district, or admin..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10"
                    data-testid="input-search-students"
                  />
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Students Table */}
          <Card>
            <CardHeader>
              <CardTitle>Students ({filteredStudents.length})</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="overflow-x-auto">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Merit #</TableHead>
                      <TableHead>Student Name</TableHead>
                      <TableHead>App No</TableHead>
                      <TableHead>Stream</TableHead>
                      <TableHead>Current District</TableHead>
                      <TableHead>District Admin</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Lock Status</TableHead>
                      <TableHead>Choices</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {isLoading ? (
                      <TableRow>
                        <TableCell colSpan={10} className="text-center py-8">
                          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
                        </TableCell>
                      </TableRow>
                    ) : filteredStudents.length === 0 ? (
                      <TableRow>
                        <TableCell colSpan={10} className="text-center py-8 text-muted-foreground">
                          No students found
                        </TableCell>
                      </TableRow>
                    ) : (
                      filteredStudents.map((student: Student) => (
                        <TableRow key={student.id}>
                          <TableCell className="font-mono">
                            {student.meritNumber}
                          </TableCell>
                          <TableCell className="font-medium">
                            {student.name}
                          </TableCell>
                          <TableCell className="font-mono">
                            {student.appNo}
                          </TableCell>
                          <TableCell>
                            {student.stream ? (
                              <Badge variant="outline">{student.stream}</Badge>
                            ) : (
                              <Badge variant="destructive">Not Set</Badge>
                            )}
                          </TableCell>
                          <TableCell>
                            {student.counselingDistrict || (
                              <span className="text-muted-foreground">Not Assigned</span>
                            )}
                          </TableCell>
                          <TableCell>
                            {student.districtAdmin || (
                              <span className="text-muted-foreground">Not Assigned</span>
                            )}
                          </TableCell>
                          <TableCell>
                            {getStatusBadge(student.allocationStatus || 'pending')}
                          </TableCell>
                          <TableCell>
                            {student.lockedBy ? (
                              <Badge variant="secondary" className="bg-blue-100 text-blue-800">
                                <Lock className="w-3 h-3 mr-1" />
                                Locked
                              </Badge>
                            ) : (
                              <Badge variant="outline">
                                <Unlock className="w-3 h-3 mr-1" />
                                Unlocked
                              </Badge>
                            )}
                          </TableCell>
                          <TableCell>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => openChoicesModal(student)}
                              className="p-1 h-6 w-6"
                              data-testid={`button-view-choices-${student.id}`}
                            >
                              <Eye className="w-3 h-3" />
                            </Button>
                          </TableCell>
                          <TableCell>
                            <div className="flex items-center space-x-2">
                              {/* Case 1: Student is locked - show only unlock button */}
                              {user?.role === 'central_admin' && student.lockedBy ? (
                                <Button
                                  variant="outline"
                                  size="sm"
                                  onClick={() => {
                                    setSelectedStudentForUnlock(student);
                                    setIsUnlockConfirmDialogOpen(true);
                                  }}
                                  disabled={unlockEditMutation.isPending}
                                  data-testid={`button-unlock-${student.id}`}
                                >
                                  <Unlock className="w-4 h-4 mr-1" />
                                  Unlock
                                </Button>
                              ) : user?.role === 'central_admin' && 
                                        student.counselingDistrict === 'Mohali' && 
                                        student.districtAdmin === 'Central_admin' && 
                                        areAllPreferencesFilled(student) ? (
                                /* Case 2: Central admin with filled preferences and not locked - show lock + release buttons */
                                <>
                                  <Button
                                    variant="outline"
                                    size="sm"
                                    onClick={() => {
                                      setSelectedStudentForLock(student);
                                      setIsLockConfirmDialogOpen(true);
                                    }}
                                    disabled={lockForEditMutation.isPending}
                                    data-testid={`button-lock-${student.id}`}
                                  >
                                    <Lock className="w-4 h-4 mr-1" />
                                    Lock
                                  </Button>

                                  <Button
                                    variant="outline"
                                    size="sm"
                                    onClick={() => {
                                      setSelectedStudentForRelease(student);
                                      setIsReleaseConfirmDialogOpen(true);
                                    }}
                                    disabled={releaseAssignmentMutation.isPending}
                                    data-testid={`button-release-${student.id}`}
                                    className="text-red-600 border-red-300 hover:bg-red-50"
                                  >
                                    <XCircle className="w-4 h-4 mr-1" />
                                    Release
                                  </Button>
                                </>
                              ) : (
                                /* Case 3: Regular edit button for all other cases */
                                canEditStudent(student) && !student.lockedBy ? (
                                  <Button
                                    variant="outline"
                                    size="sm"
                                    onClick={() => openEditModal(student)}
                                    data-testid={`button-edit-${student.id}`}
                                  >
                                    <Edit className="w-4 h-4 mr-1" />
                                    Edit
                                  </Button>
                                ) : (
                                  <Button
                                    variant="ghost"
                                    size="sm"
                                    disabled
                                    data-testid={`button-edit-disabled-${student.id}`}
                                    className="text-muted-foreground"
                                  >
                                    <Edit className="w-4 h-4 mr-1" />
                                    Edit
                                  </Button>
                                )
                              )}
                            </div>
                          </TableCell>
                        </TableRow>
                      ))
                    )}
                  </TableBody>
                </Table>
              </div>
            </CardContent>
          </Card>
        </div>
      </main>

      {/* Edit Modal */}
      <Dialog open={isEditModalOpen} onOpenChange={setIsEditModalOpen}>
        <DialogContent className="sm:max-w-4xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Edit Student Preferences</DialogTitle>
          </DialogHeader>

          {selectedStudentForEdit && (
            <Form {...form}>
              <form onSubmit={form.handleSubmit(handleModalSave)} className="space-y-4">
                <div className="grid grid-cols-2 gap-4 p-4 bg-muted rounded-lg">
                  <div>
                    <p className="text-sm font-medium">Student Name</p>
                    <p className="text-lg">{selectedStudentForEdit.name}</p>
                  </div>
                  <div>
