import fs from 'fs';
import path from 'path';
import bcrypt from 'bcrypt';
import { storage } from './storage';
import type { InsertUser } from '@shared/schema';

interface CredentialUser {
  username: string;
  email: string;
  password: string;
  role: string;
  district?: string;
  firstName: string;
  lastName: string;
}

interface Credentials {
  central_admin: CredentialUser;
  district_admins: CredentialUser[];
}

async function seedUsers() {
  try {
    console.log('ðŸŒ± Starting user seeding...');

    // Read credentials.json
    const credentialsData = fs.readFileSync(path.join(import.meta.dirname, '..', 'credentials.json'), 'utf-8');
    const credentials: Credentials = JSON.parse(credentialsData);

    const usersToSeed: CredentialUser[] = [
      credentials.central_admin,
      ...credentials.district_admins
    ];

    let seedCount = 0;

    for (const userData of usersToSeed) {
      // Check if user already exists
      const existingUser = await storage.getUserByUsername(userData.username);
      if (existingUser) {
        console.log(`â­ï¸  User ${userData.username} already exists, skipping...`);
        continue;
      }

      // Hash the password
      const hashedPassword = await bcrypt.hash(userData.password, 10);

      // Prepare user data (excluding password from credentials for security)
      const { password: _, ...safeCredentials } = userData;
      const newUser: InsertUser = {
        username: userData.username,
        email: userData.email,
        password: hashedPassword,
        role: userData.role as 'central_admin' | 'district_admin',
        district: userData.district || null,
        firstName: userData.firstName,
        lastName: userData.lastName,
        credentials: safeCredentials, // Store credentials without plaintext password
        isBlocked: false,
      };

      // Create user
      const createdUser = await storage.createUser(newUser);
      console.log(`âœ… Created user: ${createdUser.username} (${createdUser.role})`);
      seedCount++;
    }

    console.log(`ðŸŽ‰ Seeding completed! Added ${seedCount} new users.`);
    process.exit(0);
  } catch (error) {
    console.error('âŒ Seeding failed:', error);
    process.exit(1);
  }
}

// Run the seeder
seedUsers();

