import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { 
  Pagination, 
  PaginationContent, 
  PaginationItem, 
  PaginationLink, 
  PaginationNext, 
  PaginationPrevious, 
  PaginationEllipsis 
} from "@/components/ui/pagination";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { CheckCircle, XCircle, AlertTriangle, FileSpreadsheet, X, Upload } from "lucide-react";

interface DataPreviewModalProps {
  isOpen: boolean;
  onClose: () => void;
  onConfirm: () => void;
  validationResults: {
    isValid: boolean;
    message: string;
    errors: string[];
    warnings: string[];
    recordCount: number;
    allRecords: any[];
  };
  fileType: 'students' | 'vacancies' | 'entrance-results';
  fileName: string;
  isUploading: boolean;
}

const ITEMS_PER_PAGE = 20;

export function DataPreviewModal({
  isOpen,
  onClose,
  onConfirm,
  validationResults,
  fileType,
  fileName,
  isUploading
}: DataPreviewModalProps) {
  const [currentPage, setCurrentPage] = useState(1);

  const totalPages = Math.ceil((validationResults.allRecords?.length || 0) / ITEMS_PER_PAGE);
  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;
  const currentRecords = validationResults.allRecords?.slice(startIndex, endIndex) || [];

  const renderStudentTable = (records: any[]) => (
    <div className="overflow-x-auto">
      <table className="w-full text-sm border-collapse">
        <thead>
          <tr className="border-b bg-muted/50">
            <th className="p-2 text-left font-medium">App No</th>
            <th className="p-2 text-left font-medium">Merit</th>
            <th className="p-2 text-left font-medium">Name</th>
            <th className="p-2 text-left font-medium">Stream</th>
            <th className="p-2 text-left font-medium">Gender</th>
            <th className="p-2 text-left font-medium">Category</th>
            <th className="p-2 text-left font-medium">Choice 1</th>
            <th className="p-2 text-left font-medium">Choice 2</th>
            <th className="p-2 text-left font-medium">Choice 3</th>
          </tr>
        </thead>
        <tbody>
          {records.map((record, index) => (
            <tr key={index} className="border-b hover:bg-muted/20">
              <td className="p-2">{record.appNo || '-'}</td>
              <td className="p-2">{record.meritNumber || '-'}</td>
              <td className="p-2 font-medium">{record.name || '-'}</td>
              <td className="p-2">
                <Badge variant="outline">{record.stream || '-'}</Badge>
              </td>
              <td className="p-2">{record.gender || '-'}</td>
              <td className="p-2">{record.category || '-'}</td>
              <td className="p-2">{record.choice1 || '-'}</td>
              <td className="p-2">{record.choice2 || '-'}</td>
              <td className="p-2">{record.choice3 || '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const renderVacancyTable = (records: any[]) => (
    <div className="overflow-x-auto">
      <table className="w-full text-sm border-collapse">
        <thead>
          <tr className="border-b bg-muted/50">
            <th className="p-2 text-left font-medium">District</th>
            <th className="p-2 text-left font-medium">Stream</th>
            <th className="p-2 text-left font-medium">Gender</th>
            <th className="p-2 text-left font-medium">Category</th>
            <th className="p-2 text-center font-medium">Total Seats</th>
            <th className="p-2 text-center font-medium">Available Seats</th>
          </tr>
        </thead>
        <tbody>
          {records.map((record, index) => (
            <tr key={index} className="border-b hover:bg-muted/20">
              <td className="p-2 font-medium">{record.district || '-'}</td>
              <td className="p-2">
                <Badge variant="outline">{record.stream || '-'}</Badge>
              </td>
              <td className="p-2">{record.gender || '-'}</td>
              <td className="p-2">{record.category || '-'}</td>
              <td className="p-2 text-center font-mono">{record.totalSeats || 0}</td>
              <td className="p-2 text-center font-mono">{record.availableSeats || 0}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const renderEntranceResultsTable = (records: any[]) => (
    <div className="overflow-x-auto">
      <table className="w-full text-sm border-collapse">
        <thead>
          <tr className="border-b bg-muted/50">
            <th className="p-2 text-left font-medium">Merit No</th>
            <th className="p-2 text-left font-medium">App No</th>
            <th className="p-2 text-left font-medium">Roll No</th>
            <th className="p-2 text-left font-medium">Student Name</th>
            <th className="p-2 text-center font-medium">Marks</th>
            <th className="p-2 text-left font-medium">Gender</th>
            <th className="p-2 text-left font-medium">Category</th>
            <th className="p-2 text-left font-medium">Stream</th>
          </tr>
        </thead>
        <tbody>
          {records.map((record, index) => (
            <tr key={index} className="border-b hover:bg-muted/20">
              <td className="p-2 font-mono">{record.meritNo || '-'}</td>
              <td className="p-2">{record.applicationNo || '-'}</td>
              <td className="p-2 font-mono">{record.rollNo || '-'}</td>
              <td className="p-2 font-medium">{record.studentName || '-'}</td>
              <td className="p-2 text-center font-mono font-medium">{record.marks || 0}</td>
              <td className="p-2">{record.gender || '-'}</td>
              <td className="p-2">{record.category || '-'}</td>
              <td className="p-2">
                <Badge variant="outline">{record.stream || '-'}</Badge>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const renderTable = () => {
    if (!currentRecords.length) {
      return (
        <div className="text-center py-8 text-muted-foreground">
          <FileSpreadsheet className="w-12 h-12 mx-auto mb-3 opacity-50" />
          <p>No records to display</p>
        </div>
      );
    }

    switch (fileType) {
      case 'students':
        return renderStudentTable(currentRecords);
      case 'vacancies':
        return renderVacancyTable(currentRecords);
      case 'entrance-results':
        return renderEntranceResultsTable(currentRecords);
      default:
        return null;
    }
  };

  const getTitle = () => {
    switch (fileType) {
      case 'students':
        return 'Student Choices Preview';
      case 'vacancies':
        return 'Vacancy Data Preview';
      case 'entrance-results':
        return 'Entrance Results Preview';
      default:
        return 'Data Preview';
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-7xl max-h-[90vh] overflow-hidden flex flex-col">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <FileSpreadsheet className="w-5 h-5" />
              <span>{getTitle()}</span>
            </div>
            <Button
              variant="ghost"
              size="sm"
              onClick={onClose}
              data-testid="button-close-preview"
            >
              <X className="w-4 h-4" />
            </Button>
          </DialogTitle>
        </DialogHeader>

        <div className="flex-1 overflow-hidden flex flex-col space-y-4">
          {/* File Info Card */}
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm flex items-center justify-between">
                <span>File: {fileName}</span>
                <div className="flex items-center space-x-2">
                  {validationResults.isValid ? (
                    <Badge variant="default" className="bg-green-100 text-green-800 border-green-200">
                      <CheckCircle className="w-3 h-3 mr-1" />
                      Valid
                    </Badge>
                  ) : (
                    <Badge variant="destructive">
                      <XCircle className="w-3 h-3 mr-1" />
                      Invalid
                    </Badge>
                  )}
                  <Badge variant="outline">
                    {validationResults.recordCount} records
                  </Badge>
                </div>
              </CardTitle>
            </CardHeader>
            <CardContent className="pt-0">
              <p className="text-sm text-muted-foreground mb-3">
                {validationResults.message}
              </p>

              {/* Validation Errors */}
              {validationResults.errors.length > 0 && (
                <Alert variant="destructive" className="mb-3">
                  <AlertTriangle className="h-4 w-4" />
                  <AlertDescription>
                    <div className="font-medium mb-2">
                      Found {validationResults.errors.length} validation errors:
                    </div>
                    <div className="max-h-32 overflow-y-auto space-y-1">
                      {validationResults.errors.slice(0, 10).map((error, index) => (
                        <div key={index} className="text-xs font-mono bg-destructive/10 p-1 rounded">
                          {error}
                        </div>
                      ))}
                      {validationResults.errors.length > 10 && (
                        <div className="text-xs text-muted-foreground">
                          ... and {validationResults.errors.length - 10} more errors
                        </div>
                      )}
                    </div>
                  </AlertDescription>
                </Alert>
              )}

              {/* Warnings */}
              {validationResults.warnings?.length > 0 && (
                <Alert className="mb-3">
                  <AlertTriangle className="h-4 w-4" />
                  <AlertDescription>
                    <div className="font-medium mb-2">Warnings:</div>
                    <div className="space-y-1">
                      {validationResults.warnings.map((warning, index) => (
                        <div key={index} className="text-xs">{warning}</div>
                      ))}
                    </div>
                  </AlertDescription>
                </Alert>
              )}
            </CardContent>
          </Card>

          {/* Data Table */}
          <Card className="flex-1 overflow-hidden flex flex-col">
            <CardHeader className="pb-3">
              <CardTitle className="text-sm flex items-center justify-between">
                <span>Records {startIndex + 1}-{Math.min(endIndex, validationResults.recordCount)} of {validationResults.recordCount}</span>
                {totalPages > 1 && (
                  <div className="text-xs text-muted-foreground">
                    Page {currentPage} of {totalPages}
                  </div>
                )}
              </CardTitle>
            </CardHeader>
            <CardContent className="flex-1 overflow-auto">
              {renderTable()}
            </CardContent>
          </Card>

          {/* Pagination */}
          {totalPages > 1 && (
            <Pagination>
              <PaginationContent>
                <PaginationItem>
                  <PaginationPrevious 
                    onClick={() => currentPage > 1 && setCurrentPage(currentPage - 1)}
                    className={currentPage === 1 ? "pointer-events-none opacity-50" : "cursor-pointer"}
                  />
                </PaginationItem>

                {/* Page Numbers */}
                {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                  const pageNum = Math.max(1, Math.min(totalPages - 4, currentPage - 2)) + i;
                  if (pageNum > totalPages) return null;

                  return (
                    <PaginationItem key={pageNum}>
                      <PaginationLink
                        onClick={() => setCurrentPage(pageNum)}
                        isActive={currentPage === pageNum}
                        className="cursor-pointer"
                      >
                        {pageNum}
                      </PaginationLink>
                    </PaginationItem>
                  );
                })}

                {totalPages > 5 && currentPage < totalPages - 2 && (
                  <PaginationItem>
                    <PaginationEllipsis />
                  </PaginationItem>
                )}

                <PaginationItem>
                  <PaginationNext 
                    onClick={() => currentPage < totalPages && setCurrentPage(currentPage + 1)}
                    className={currentPage === totalPages ? "pointer-events-none opacity-50" : "cursor-pointer"}
                  />
                </PaginationItem>
              </PaginationContent>
            </Pagination>
          )}
        </div>

        {/* Action Buttons */}
        <div className="flex justify-end space-x-3 pt-4 border-t">
          <Button
            variant="outline"
            onClick={onClose}
            disabled={isUploading}
            data-testid="button-cancel-upload"
          >
            Cancel
          </Button>
          <Button
            onClick={onConfirm}
            disabled={!validationResults.isValid || isUploading}
            data-testid="button-confirm-upload"
          >
            {isUploading ? (
              <>
                <div className="w-4 h-4 mr-2 border-2 border-white border-t-transparent rounded-full animate-spin" />
                Uploading...
              </>
            ) : (
              <>
                <Upload className="w-4 h-4 mr-2" />
                Confirm & Upload to Database
              </>
            )}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/pages/profile.tsx.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { PasswordInput } from '@/components/ui/password-input';
import { Label } from '@/components/ui/label';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Separator } from '@/components/ui/separator';
import { Badge } from '@/components/ui/badge';
import { User, Lock, Upload } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { apiRequest, queryClient } from '@/lib/queryClient';

interface User {
  id: string;
  username: string;
  email: string;
  role: string;
  district?: string;
  firstName: string;
  lastName: string;
}

export default function Profile() {
  const { toast } = useToast();
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [csvFile, setCsvFile] = useState<File | null>(null);
  const [defaultPassword, setDefaultPassword] = useState('Punjab@2024');

  // Get current user
  const { data: user, isLoading: userLoading } = useQuery<User>({
    queryKey: ['/api/auth/user'],
  });

  // Change password mutation
  const changePasswordMutation = useMutation({
    mutationFn: async (data: { currentPassword: string; newPassword: string }) => {
      return apiRequest('PUT', '/api/auth/change-password', data);
    },
    onSuccess: () => {
      toast({
        title: 'Success',
        description: 'Password changed successfully',
      });
      setCurrentPassword('');
      setNewPassword('');
      setConfirmPassword('');
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to change password',
        variant: 'destructive',
      });
    },
  });

  // CSV import mutation (only for central admin)
  const importUsersMutation = useMutation({
    mutationFn: async (data: FormData) => {
      const response = await fetch('/api/users/import', {
        method: 'POST',
        body: data,
      });
      if (!response.ok) {
        throw new Error(await response.text());
      }
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: 'Import Complete',
        description: `Imported ${data.importedCount} users, skipped ${data.skippedCount}`,
      });
      setCsvFile(null);
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });
    },
    onError: (error: any) => {
      toast({
        title: 'Import Failed',
        description: error.message || 'Failed to import users',
        variant: 'destructive',
      });
    },
  });

  const handlePasswordChange = (e: React.FormEvent) => {
    e.preventDefault();

    if (newPassword !== confirmPassword) {
      toast({
        title: 'Error',
        description: 'New passwords do not match',
        variant: 'destructive',
      });
      return;
    }

    if (newPassword.length < 6) {
      toast({
        title: 'Error',
        description: 'Password must be at least 6 characters long',
        variant: 'destructive',
      });
      return;
    }

    changePasswordMutation.mutate({ currentPassword, newPassword });
  };

  const handleCsvImport = (e: React.FormEvent) => {
    e.preventDefault();

    if (!csvFile) {
      toast({
        title: 'Error',
        description: 'Please select a CSV file',
        variant: 'destructive',
      });
      return;
    }

    const formData = new FormData();
    formData.append('file', csvFile);
    formData.append('defaultPassword', defaultPassword);

    importUsersMutation.mutate(formData);
  };

  if (userLoading) {
    return <div data-testid="loading-profile">Loading profile...</div>;
  }

  if (!user) {
    return <div data-testid="error-no-user">No user data available</div>;
  }

  return (
    <div className="container mx-auto py-8 space-y-6" data-testid="profile-page">
      <div className="flex items-center gap-3 mb-6">
        <User className="h-8 w-8 text-primary" />
        <h1 className="text-3xl font-bold">Profile Settings</h1>
      </div>

      {/* User Information */}
      <Card data-testid="card-user-info">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <User className="h-5 w-5" />
            User Information
          </CardTitle>
          <CardDescription>Your account details and role information</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label>Username</Label>
              <div className="font-medium" data-testid="text-username">{user.username}</div>
            </div>
            <div>
              <Label>Email</Label>
              <div className="font-medium" data-testid="text-email">{user.email || 'Not set'}</div>
            </div>
            <div>
              <Label>Role</Label>
              <div>
                <Badge variant={user.role === 'central_admin' ? 'default' : 'secondary'} data-testid="badge-role">
                  {user.role.replace('_', ' ').toUpperCase()}
                </Badge>
              </div>
            </div>
            <div>
              <Label>District</Label>
              <div className="font-medium" data-testid="text-district">{user.district || 'N/A'}</div>
            </div>
            <div>
              <Label>First Name</Label>
              <div className="font-medium" data-testid="text-firstname">{user.firstName}</div>
            </div>
            <div>
              <Label>Last Name</Label>
              <div className="font-medium" data-testid="text-lastname">{user.lastName}</div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Change Password */}
      <Card data-testid="card-change-password">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Lock className="h-5 w-5" />
            Change Password
          </CardTitle>
          <CardDescription>Update your account password</CardDescription>
        </CardHeader>
        <CardContent>
          <form onSubmit={handlePasswordChange} className="space-y-4">
            <div>
              <Label htmlFor="current-password">Current Password</Label>
              <PasswordInput
                id="current-password"
                value={currentPassword}
                onChange={(e) => setCurrentPassword(e.target.value)}
                required
                data-testid="input-current-password"
              />
            </div>
            <div>
              <Label htmlFor="new-password">New Password</Label>
              <PasswordInput
                id="new-password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
                minLength={6}
                data-testid="input-new-password"
              />
            </div>
            <div>
              <Label htmlFor="confirm-password">Confirm New Password</Label>
              <PasswordInput
                id="confirm-password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
                minLength={6}
                data-testid="input-confirm-password"
              />
            </div>
            <Button
              type="submit"
              disabled={changePasswordMutation.isPending}
              data-testid="button-change-password"
            >
              {changePasswordMutation.isPending ? 'Changing...' : 'Change Password'}
            </Button>
          </form>
        </CardContent>
      </Card>

      {/* CSV User Import - Only for Central Admin */}
      {user.role === 'central_admin' && (
        <Card data-testid="card-csv-import">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Upload className="h-5 w-5" />
              Import Users from CSV
            </CardTitle>
            <CardDescription>
              Import multiple users from a CSV file. All users will have the same default password.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Alert className="mb-4">
              <AlertDescription>
                CSV should contain columns: username, email, role, district, firstName, lastName
              </AlertDescription>
            </Alert>
            <form onSubmit={handleCsvImport} className="space-y-4">
              <div>
                <Label htmlFor="default-password">Default Password for All Users</Label>
                <Input
                  id="default-password"
                  type="text"
                  value={defaultPassword}
                  onChange={(e) => setDefaultPassword(e.target.value)}
                  required
                  data-testid="input-default-password"
                />
              </div>
              <div>
                <Label htmlFor="csv-file">CSV File</Label>
                <Input
                  id="csv-file"
                  type="file"
                  accept=".csv,.xlsx,.xls"
                  onChange={(e) => setCsvFile(e.target.files?.[0] || null)}
                  required
                  data-testid="input-csv-file"
                />
              </div>
              <Button
                type="submit"
                disabled={importUsersMutation.isPending || !csvFile}
                data-testid="button-import-users"
              >
                {importUsersMutation.isPending ? 'Importing...' : 'Import Users'}
              </Button>
            </form>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/district-analysis.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { formatDistanceToNow } from "date-fns";
import Header from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  BarChart3, 
  Users, 
  MapPin, 
  CheckCircle, 
  AlertTriangle,
  TrendingUp,
  Clock,
} from "lucide-react";
import type { DistrictStatus, Student } from "@shared/schema";

export default function DistrictAnalysis() {
  // Fetch district statuses
  const { data: districtStatuses, isLoading: loadingStatuses } = useQuery<DistrictStatus[]>({
    queryKey: ["/api/district-status"],
  });

  // Fetch students data
  const { data: studentsResponse } = useQuery<{ students: Student[] } | Student[]>({
    queryKey: ["/api/students"],
  });

  // Fetch vacancies data
  const { data: vacancies } = useQuery<any[]>({
    queryKey: ["/api/vacancies"],
  });

  // Handle different API response formats
  const students = Array.isArray(studentsResponse) ? studentsResponse : (studentsResponse as any)?.students || [];

  // Calculate stream-wise totals
  const streamTotals = students.reduce((acc: any, student: any) => {
    if (!student.stream) return acc;
    acc[student.stream] = (acc[student.stream] || 0) + 1;
    return acc;
  }, {} as Record<string, number>);

  // Calculate district-wise, stream-wise breakdown
  const districtBreakdown = districtStatuses?.map(district => {
    const districtStudents = students.filter((s: any) => s.counselingDistrict === district.district);
    const streamBreakdown = {
      Medical: districtStudents.filter((s: any) => s.stream === 'Medical').length,
      Commerce: districtStudents.filter((s: any) => s.stream === 'Commerce').length,
      NonMedical: districtStudents.filter((s: any) => s.stream === 'NonMedical').length,
    };

    const studentsWithChoices = districtStudents.filter((s: any) => 
      s.choice1 || s.choice2 || s.choice3 || s.choice4 || s.choice5 ||
      s.choice6 || s.choice7 || s.choice8 || s.choice9 || s.choice10
    );

    const lockedStudents = districtStudents.filter((s: any) => s.isLocked);

    const districtVacancies = vacancies?.filter(v => v.district === district.district) || [];
    const totalSeats = districtVacancies.reduce((sum, v) => sum + (v.totalSeats || 0), 0);

    return {
      ...district,
      students: districtStudents,
      streamBreakdown,
      studentsWithChoices: studentsWithChoices.length,
      lockedStudents: lockedStudents.length,
      choicesPercentage: districtStudents.length > 0 ? (studentsWithChoices.length / districtStudents.length * 100) : 0,
      lockedPercentage: districtStudents.length > 0 ? (lockedStudents.length / districtStudents.length * 100) : 0,
      totalSeats,
    };
  }) || [];

  // Calculate overall metrics
  const totalStudents = students.length;
  const totalStudentsWithChoices = students.filter((s: any) => 
    s.choice1 || s.choice2 || s.choice3 || s.choice4 || s.choice5 ||
    s.choice6 || s.choice7 || s.choice8 || s.choice9 || s.choice10
  ).length;
  const totalLockedStudents = students.filter((s: any) => s.isLocked).length;
  const finalizedDistricts = districtStatuses?.filter(ds => ds.isFinalized).length || 0;
  const totalDistricts = districtStatuses?.length || 0;

  const getStatusIcon = (isFinalized: boolean) => {
    return isFinalized ? (
      <CheckCircle className="w-4 h-4 text-green-500" />
    ) : (
      <AlertTriangle className="w-4 h-4 text-amber-500" />
    );
  };

  const getFinalizationBadge = (isFinalized: boolean) => {
    return (
      <Badge variant={isFinalized ? "secondary" : "outline"} 
             className={isFinalized ? "bg-green-100 text-green-800" : "bg-amber-100 text-amber-800"}>
        {isFinalized ? "Finalized" : "Pending"}
      </Badge>
    );
  };

  return (
    <div className="flex-1 flex flex-col">
      <Header 
        title="District Analysis" 
        breadcrumbs={[
          { name: "Home" },
          { name: "Reports" },
          { name: "District Analysis" }
        ]}
      />

      <main className="flex-1 p-6 overflow-auto">
        <div className="space-y-6">
          {/* Overview Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <Card>
              <CardContent className="pt-6">
                <div className="flex items-center space-x-2">
                  <Users className="w-5 h-5 text-blue-500" />
                  <div>
                    <p className="text-2xl font-bold" data-testid="total-students">
                      {totalStudents.toLocaleString()}
                    </p>
                    <p className="text-xs text-muted-foreground">Total Students</p>
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="flex items-center space-x-2">
                  <CheckCircle className="w-5 h-5 text-green-500" />
                  <div>
                    <p className="text-2xl font-bold" data-testid="students-with-choices">
                      {totalStudentsWithChoices.toLocaleString()}
                    </p>
                    <p className="text-xs text-muted-foreground">With Preferences</p>
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="flex items-center space-x-2">
                  <MapPin className="w-5 h-5 text-amber-500" />
                  <div>
                    <p className="text-2xl font-bold" data-testid="finalized-districts">
                      {finalizedDistricts}/{totalDistricts}
                    </p>
                    <p className="text-xs text-muted-foreground">Districts Finalized</p>
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="flex items-center space-x-2">
                  <BarChart3 className="w-5 h-5 text-purple-500" />
                  <div>
                    <p className="text-2xl font-bold" data-testid="locked-students">
                      {totalLockedStudents.toLocaleString()}
                    </p>
                    <p className="text-xs text-muted-foreground">Locked Students</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Stream-wise Overview */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <TrendingUp className="w-5 h-5 mr-2 text-primary" />
                Stream-wise Distribution
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {Object.entries(streamTotals).map(([stream, count]) => (
                  <div key={stream} className="text-center p-4 bg-gray-50 dark:bg-gray-800 rounded-lg">
                    <p className="text-2xl font-bold text-primary" data-testid={`stream-${stream.toLowerCase()}-count`}>
                      {(count as number).toLocaleString()}
                    </p>
                    <p className="text-sm text-muted-foreground">{stream}</p>
                    <p className="text-xs text-muted-foreground">
                      {totalStudents > 0 ? ((count as number / totalStudents * 100)).toFixed(1) : 0}%
                    </p>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* District-wise Breakdown */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <div className="flex items-center">
                  <MapPin className="w-5 h-5 mr-2 text-primary" />
                  District-wise Analysis
                </div>
                <Badge variant={finalizedDistricts === totalDistricts ? "default" : "destructive"}>
                  {finalizedDistricts}/{totalDistricts} Finalized
                </Badge>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <Tabs defaultValue="overview" className="w-full">
                <TabsList>
                  <TabsTrigger value="overview">Overview</TabsTrigger>
                  <TabsTrigger value="streams">Stream Distribution</TabsTrigger>
                  <TabsTrigger value="capacity">Seat Capacity</TabsTrigger>
                </TabsList>

                <TabsContent value="overview" className="mt-6">
                  <div className="space-y-4">
                    {loadingStatuses ? (
                      <div className="flex items-center justify-center p-8">
                        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
                      </div>
                    ) : (
                      districtBreakdown.map((district) => (
                        <Card key={district.id} className={`border-l-4 ${district.isFinalized ? 'border-l-green-500' : 'border-l-amber-500'}`}>
                          <CardContent className="pt-4">
                            <div className="flex items-start justify-between">
                              <div className="flex-1">
                                <div className="flex items-center space-x-2 mb-2">
                                  {getStatusIcon(!!district.isFinalized)}
                                  <h3 className="font-semibold">{district.district}</h3>
                                  {getFinalizationBadge(!!district.isFinalized)}
                                  {district.isFinalized && district.finalizedAt && (
                                    <div className="flex items-center text-xs text-muted-foreground">
                                      <Clock className="w-3 h-3 mr-1" />
                                      {formatDistanceToNow(new Date(district.finalizedAt), { addSuffix: true })}
                                    </div>
                                  )}
                                </div>

                                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                                  <div>
                                    <span className="text-muted-foreground">Total Students:</span>
                                    <p className="font-medium" data-testid={`district-${district.district}-total`}>
                                      {district.students.length.toLocaleString()}
                                    </p>
                                  </div>
                                  <div>
                                    <span className="text-muted-foreground">With Preferences:</span>
                                    <p className="font-medium" data-testid={`district-${district.district}-with-choices`}>
                                      {district.studentsWithChoices} ({district.choicesPercentage.toFixed(1)}%)
                                    </p>
                                  </div>
                                  <div>
                                    <span className="text-muted-foreground">Locked:</span>
                                    <p className="font-medium" data-testid={`district-${district.district}-locked`}>
                                      {district.lockedStudents} ({district.lockedPercentage.toFixed(1)}%)
                                    </p>
                                  </div>
                                  <div>
                                    <span className="text-muted-foreground">Available Seats:</span>
                                    <p className="font-medium" data-testid={`district-${district.district}-seats`}>
                                      {district.totalSeats.toLocaleString()}
                                    </p>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </CardContent>
                        </Card>
                      ))
                    )}
                  </div>
                </TabsContent>

                <TabsContent value="streams" className="mt-6">
                  <div className="space-y-4">
                    {districtBreakdown.map((district) => (
                      <Card key={`${district.id}-streams`}>
                        <CardContent className="pt-4">
                          <div className="flex items-center space-x-2 mb-3">
                            {getStatusIcon(!!district.isFinalized)}
                            <h3 className="font-semibold">{district.district}</h3>
                            {getFinalizationBadge(!!district.isFinalized)}
                            {district.isFinalized && district.finalizedAt && (
                              <div className="flex items-center text-xs text-muted-foreground">
                                <Clock className="w-3 h-3 mr-1" />
                                {formatDistanceToNow(new Date(district.finalizedAt), { addSuffix: true })}
                              </div>
                            )}
                          </div>

                          <div className="grid grid-cols-3 gap-4">
                            {Object.entries(district.streamBreakdown).map(([stream, count]) => (
                              <div key={stream} className="text-center p-3 bg-gray-50 dark:bg-gray-800 rounded">
                                <p className="text-lg font-bold" data-testid={`district-${district.district}-${stream.toLowerCase()}`}>
                                  {count}
                                </p>
                                <p className="text-sm text-muted-foreground">{stream}</p>
                                <p className="text-xs text-muted-foreground">
                                  {district.students.length > 0 ? ((count / district.students.length) * 100).toFixed(1) : 0}%
                                </p>
                              </div>
                            ))}
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                </TabsContent>

                <TabsContent value="capacity" className="mt-6">
                  <div className="space-y-4">
                    {districtBreakdown.map((district) => {
                      const demandRatio = district.totalSeats > 0 ? (district.students.length / district.totalSeats) : 0;
                      return (
                        <Card key={`${district.id}-capacity`}>
                          <CardContent className="pt-4">
                            <div className="flex items-center justify-between mb-3">
                              <div className="flex items-center space-x-2">
                                {getStatusIcon(!!district.isFinalized)}
                                <h3 className="font-semibold">{district.district}</h3>
                                {district.isFinalized && district.finalizedAt && (
                                  <div className="flex items-center text-xs text-muted-foreground">
                                    <Clock className="w-3 h-3 mr-1" />
                                    {formatDistanceToNow(new Date(district.finalizedAt), { addSuffix: true })}
                                  </div>
                                )}
                              </div>
                              <Badge variant={demandRatio > 1 ? "destructive" : demandRatio > 0.8 ? "outline" : "secondary"}>
                                {demandRatio > 1 ? "Over-demand" : demandRatio > 0.8 ? "High Demand" : "Normal"}
                              </Badge>
                            </div>

                            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                              <div>
                                <span className="text-muted-foreground">Students:</span>
                                <p className="font-medium">{district.students.length.toLocaleString()}</p>
                              </div>
                              <div>
                                <span className="text-muted-foreground">Available Seats:</span>
                                <p className="font-medium">{district.totalSeats.toLocaleString()}</p>
                              </div>
                              <div>
                                <span className="text-muted-foreground">Demand Ratio:</span>
                                <p className="font-medium">{demandRatio.toFixed(2)}:1</p>
                              </div>
                              <div>
                                <span className="text-muted-foreground">Capacity:</span>
                                <p className={`font-medium ${demandRatio > 1 ? 'text-red-600' : 'text-green-600'}`}>
                                  {district.totalSeats > 0 ? ((district.totalSeats / district.students.length) * 100).toFixed(1) : 0}%
                                </p>
                              </div>
                            </div>
                          </CardContent>
                        </Card>
                      );
                    })}
                  </div>
                </TabsContent>
              </Tabs>
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

